const Tasks = () => {
  const [tasks, setTasks] = useState([{
    id:1,
    text: 'text_1',
    day: '1:1',
    reminder: true
  },
  {
    id:2,
    text: 'text_2',
    day: '2:2',
    reminder: false
  },]

  )

  return (
    <>
      {tasks.map((task) => (
        <h3 key={task.id}>{task.text}</h3>
        ))
      }
    </>
  )
}

export default Tasks
